            cell.Style.Font.Bold = true;

            cell = ws.Cell("B" + rowNumber).SetValue("DOCTYPE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("C" + rowNumber).SetValue("DOC #");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("D" + rowNumber).SetValue("DOC DATE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("E" + rowNumber).SetValue("DEBTORCODE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("F" + rowNumber).SetValue("DEBTORNAME");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("G" + rowNumber).SetValue("STOCKCODE");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("H" + rowNumber).SetValue("STOCKNAME");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("I" + rowNumber).SetValue("SALES QTY");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("J" + rowNumber).SetValue("SALES VALUE");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("K" + rowNumber).SetValue("ITEM UNIT PRICE");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("L" + rowNumber).SetValue("ITEM DISCOUNT");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("M" + rowNumber).SetValue("DESCRIPTION");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("N" + rowNumber).SetValue("PROJECT CODE");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("O" + rowNumber).SetValue("STOCK LOCATIONCODE");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("P" + rowNumber).SetValue("DEBTOR CATEGORY");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("Q" + rowNumber).SetValue("CUSTOMER AREA");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("R" + rowNumber).SetValue("STOCK CLASS");
            cell.Style.Font.Bold = true;


            #endregion
            foreach (SALESANALYSISRESULT result in data)
            {
                rowNumber++;
                cell = ws.Cell("A" + rowNumber).SetValue(result.DocSalesPersonCode);
                cell = ws.Cell("B" + rowNumber).SetValue(result.DocType);
                cell = ws.Cell("C" + rowNumber).SetValue(result.DocumentCode);
                cell = ws.Cell("D" + rowNumber).SetValue(result.DocumentDate);
                cell = ws.Cell("E" + rowNumber).SetValue(result.CustomerCode);
                cell = ws.Cell("F" + rowNumber).SetValue(result.CustomerName);
                cell = ws.Cell("G" + rowNumber).SetValue(result.StockCode);
                cell = ws.Cell("H" + rowNumber).SetValue(result.StockName);
                cell = ws.Cell("I" + rowNumber).SetValue(result.ItemQty);
                cell = ws.Cell("J" + rowNumber).SetValue(result.ItemValue);
                cell = ws.Cell("K" + rowNumber).SetValue(result.ItemUnitPrice);
                cell = ws.Cell("L" + rowNumber).SetValue(result.ItemDiscountAmount);
                cell = ws.Cell("M" + rowNumber).SetValue(result.Description);
                cell = ws.Cell("N" + rowNumber).SetValue(result.ProjectCode);
                cell = ws.Cell("O" + rowNumber).SetValue(result.StockLocationName);
                cell = ws.Cell("P" + rowNumber).SetValue(result.DebtorCategoryName);
                cell = ws.Cell("Q" + rowNumber).SetValue(result.DebtorAreaCode);
                cell = ws.Cell("R" + rowNumber).SetValue(result.StockClassName);


            }

            ws.Column("A").Width = 15;
            ws.Column("B").Width = 15;
            ws.Column("C").Width = 15;
            ws.Column("D").Width = 15;
            ws.Column("E").Width = 15;
            ws.Column("F").Width = 15;
            ws.Column("G").Width = 15;
            ws.Column("H").Width = 15;
            ws.Column("I").Width = 15;
            ws.Column("J").Width = 15;
            ws.Column("K").Width = 15;
            ws.Column("L").Width = 15;
