    SALESANALYSISFILTER formModel = new();


    private List<SalesPersons> salesPersons { get; set; } = new List<SalesPersons>();
    private List<Debtors> debtors { get; set; } = new List<Debtors>();

    private async Task GenerateReport()
    {
        formModel.CompanyCode = AppState.selectedCompanyCode;

        var httpRequest = await http.PostAsJsonAsync("api/Report/GenerateSalesAnalysis", formModel);

        if (httpRequest.IsSuccessStatusCode)
        {
            var responseString = await httpRequest.Content.ReadAsStringAsync();

            //Console.WriteLine(responseString);
            var data = JsonSerializer.Deserialize<List<SALESANALYSISRESULT>>(responseString,
              new JsonSerializerOptions() { PropertyNameCaseInsensitive = true });

            GenerateExcel(data);


            //GenerateExcel(Result);

        }
    }

    private async Task LoadData()
    {
        salesPersons = await http.GetFromJsonAsync<List<SalesPersons>>("api/Qne/GetSalesPersons?companyCode=" + AppState.selectedCompanyCode);
        debtors = await http.GetFromJsonAsync<List<Debtors>>("api/Qne/GetDebtors?companyCode=" + AppState.selectedCompanyCode);
    }

    private async void GenerateExcel(List<SALESANALYSISRESULT> data)
    {
        using (XLWorkbook wb = new XLWorkbook())
        {

            int rowNumber = 0;

            IXLWorksheet ws = wb.Worksheets.Add("Batch No. Balance Details");
            IXLCell cell;

            cell = ws.Cell("A" + ++rowNumber).SetValue(formModel.CompanyCode);
            cell.Style.Alignment.SetHorizontal(XLAlignmentHorizontalValues.Center);
            cell.Style.Font.Bold = true;

            cell = ws.Cell("A" + ++rowNumber).SetValue("Date From ");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("B" + rowNumber).SetValue(formModel.DateFrom?.ToShortDateString());
            cell.Style.Font.Bold = true;

            cell = ws.Cell("A" + ++rowNumber).SetValue("Date To ");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("B" + rowNumber).SetValue(formModel.DateTo?.ToShortDateString());
            cell.Style.Font.Bold = true;


            #region Excel header
            cell = ws.Cell("A" + ++rowNumber).SetValue("DOCSALEPERSON CODE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("B" + rowNumber).SetValue("DOCTYPE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("C" + rowNumber).SetValue("DOC #");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("D" + rowNumber).SetValue("DOC DATE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("E" + rowNumber).SetValue("DEBTORCODE");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("F" + rowNumber).SetValue("DEBTORNAME");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("G" + rowNumber).SetValue("STOCKCODE");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("H" + rowNumber).SetValue("STOCKNAME");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("I" + rowNumber).SetValue("SALES QTY");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("J" + rowNumber).SetValue("SALES VALUE");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("K" + rowNumber).SetValue("ITEM UNIT PRICE");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("L" + rowNumber).SetValue("ITEM DISCOUNT");
            cell.Style.Font.Bold = true;


            cell = ws.Cell("M" + rowNumber).SetValue("DESCRIPTION");
            cell.Style.Font.Bold = true;

            cell = ws.Cell("N" + rowNumber).SetValue("PROJECT CODE");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("O" + rowNumber).SetValue("STOCK LOCATIONCODE");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("P" + rowNumber).SetValue("DEBTOR CATEGORY");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("Q" + rowNumber).SetValue("CUSTOMER AREA");
            cell.Style.Font.Bold = true;
            cell = ws.Cell("R" + rowNumber).SetValue("STOCK CLASS");
            cell.Style.Font.Bold = true;


            #endregion
            foreach (SALESANALYSISRESULT result in data)
            {
                rowNumber++;
                cell = ws.Cell("A" + rowNumber).SetValue(result.DocSalesPersonCode);
                cell = ws.Cell("B" + rowNumber).SetValue(result.DocType);
                cell = ws.Cell("C" + rowNumber).SetValue(result.DocumentCode);
                cell = ws.Cell("D" + rowNumber).SetValue(result.DocumentDate);
                cell = ws.Cell("E" + rowNumber).SetValue(result.CustomerCode);
                cell = ws.Cell("F" + rowNumber).SetValue(result.CustomerName);
                cell = ws.Cell("G" + rowNumber).SetValue(result.StockCode);
                cell = ws.Cell("H" + rowNumber).SetValue(result.StockName);
                cell = ws.Cell("I" + rowNumber).SetValue(result.ItemQty);
                cell = ws.Cell("J" + rowNumber).SetValue(result.ItemValue);
                cell = ws.Cell("K" + rowNumber).SetValue(result.ItemUnitPrice);
                cell = ws.Cell("L" + rowNumber).SetValue(result.ItemDiscountAmount);
                cell = ws.Cell("M" + rowNumber).SetValue(result.Description);
                cell = ws.Cell("N" + rowNumber).SetValue(result.ProjectCode);
                cell = ws.Cell("O" + rowNumber).SetValue(result.StockLocationName);
                cell = ws.Cell("P" + rowNumber).SetValue(result.DebtorCategoryName);
                cell = ws.Cell("Q" + rowNumber).SetValue(result.DebtorAreaCode);
                cell = ws.Cell("R" + rowNumber).SetValue(result.StockClassName);


            }

            ws.Column("A").Width = 15;
            ws.Column("B").Width = 15;
            ws.Column("C").Width = 15;
            ws.Column("D").Width = 15;
            ws.Column("E").Width = 15;
            ws.Column("F").Width = 15;
            ws.Column("G").Width = 15;
            ws.Column("H").Width = 15;
            ws.Column("I").Width = 15;
            ws.Column("J").Width = 15;
            ws.Column("K").Width = 15;
            ws.Column("L").Width = 15;
            ws.Column("M").Width = 15;
            ws.Column("N").Width = 15;
            ws.Column("O").Width = 15;
            ws.Column("P").Width = 15;
            ws.Column("Q").Width = 15;
            ws.Column("R").Width = 15;


            if (wb.Worksheets.Count > 0)
            {
                MemoryStream ms = GetStream(wb);
                byte[] bytes = ms.ToArray();
                ms.Close();

                await js.SaveAsFileAsync("SALES_ANALYSIS__" + formModel.DateFrom?.ToShortDateString() + "-" + formModel.DateTo?.ToShortDateString(), bytes, "application/vnd.ms-excel");
            }
            else
            {
                await js.InvokeVoidAsync("alert", "No record found.");
            }

        }
    }
    private MemoryStream GetStream(XLWorkbook excelWorkbook)
    {
        MemoryStream fs = new MemoryStream();
        excelWorkbook.SaveAs(fs);
        fs.Position = 0;
        return fs;
    }

    protected override async Task OnInitializedAsync()
    {
        if (AppState.CompanyReady())
        {
            await LoadData();
        }
    }
    private async Task AppState_StateChanged(ComponentBase Source, string Property)
    {
        if (Source != this)
        {
            if (AppState.CompanyReady())
            {
                await LoadData();
            }
            await InvokeAsync(StateHasChanged);
        }
    }
    protected override void OnInitialized()
    {
        AppState.StateChanged += async (Source, Property) => await AppState_StateChanged(Source, Property);
    }
    void IDisposable.Dispose()
    {
        AppState.StateChanged -= async (Source, Property) => await AppState_StateChanged(Source, Property);
    }


